// Define supported graph types. Note that these types should match the d3 library types.
export const GraphType = {
    Bar: "discreteBarChart",
    Line: "lineChart",
    MultiBar: "multiBarChart",
    Pie: "pieChart",
    Table: "table",
};

// Defines supported graphs.
export const Graphs = [
    {
        name: "Bar Graph",
        type: GraphType.Bar,
    },
    {
        name: "Line Graph",
        type: GraphType.Line,
    },
    {
        name: "MultiBar Graph",
        type: GraphType.MultiBar,
    },
    {
        name: "Pie Chart Graph",
        type: GraphType.Pie,
    },
    {
        name: "Table",
        type: GraphType.Table,
    },
];

// Define default graph type.
export const DefaultGraphType = GraphType.Table;

// Defines the default graph.
export const DefaultGraph = Graphs[4]; // Table
