import {ComponentFactory, ComponentFactoryResolver} from "@angular/core";
import {Visualization} from "../visualization.interface";
import {NoVisualizationComponent} from "./none.component";

// Default implementation of the Visualizaiton interface to support
// no visualization.
export class NoneVisualization implements Visualization {
    public component: ComponentFactory<any>; /*TODO: Figure out component factory type....*/

    constructor(private componentFactoryResolver: ComponentFactoryResolver) {
        this.component = componentFactoryResolver.resolveComponentFactory(NoVisualizationComponent);
    }
}
