// Defines the type used to represent the expected Value object.
// See https://confluence.verizon.com/display/NPDNOR/Portal.
export class Value {
    public type: string;
    public value: any;
    public semantic: string;
    public background: string;

    constructor(obj?: Object) {
        this.value = "";
        this.semantic = "";
        this.background = "white";

        if (obj) {
            this.unmarshal(obj as Value);
        }
    }

    // Helper method used to assign external objects to this.
    private unmarshal(obj: Value): void {
        this.type = obj.type;
        this.value = obj.value;

        if (obj.semantic) {
            this.semantic = obj.semantic;
        }

        if (obj.background) {
            this.background = obj.background;
        }
    }
}
