import {Options} from "./options.model";
import {ExecutionOutput} from "./output.model";

export class Cell {
    public name: string;
    public language: string;
    public code: string;
    public options: Options;
    public output: ExecutionOutput;
    public inputs: Object;

    constructor(obj?: Object) {
        if (obj) {
            this.unmarshal(obj as Cell);
        } else {
            this.name = "Untitled Cell";
            this.options = new Options();
            this.output = new ExecutionOutput();
            this.code = "";
            this.inputs = {};
        }
    }

    protected unmarshal(obj: Cell) {
        this.name = obj.name;
        this.language = obj.language;
        this.code = obj.code;
        this.output = new ExecutionOutput(obj.output);
        this.options = new Options(obj.options);
        this.inputs = obj.inputs;
    }
}
