export class EditorConfig {
    public autoCloseBackets: boolean;
    public foldGutter: boolean;
    public gutters: string[];
    public lineNumbers: boolean;
    public lineWrapping: boolean;
    public matchBrackets: boolean;
    public mode: any;
    public readOnly: boolean;
    public styleActiveLine: boolean;
    public theme: string;
    public viewPortMargin: number;

    constructor(obj?:Object) {

        if(obj) {

        } else {
            this.autoCloseBackets = false;
            this.foldGutter = false;
            this.lineNumbers = false;
            this.lineWrapping = false;
            this.styleActiveLine = false;
            this.gutters =  [];
            this.theme = "solarized light";
            this.styleActiveLine = false;
            this.matchBrackets = false;
            this.viewPortMargin = Infinity;

            //Default to lua LanguageConfig
            this.mode = {
                name: "lua",
            }
        }
     

    }
}