import {EditorConfig} from './editorConfig.model';
import {LanguageMode} from "./languageMode.model";

export class LanguageConfig {
    public editorConfig: EditorConfig;
    public icon: string;
    public type: string;
    public language: string;

    public isCode() {
        return this.type === LanguageMode.Code;
    }

    public isStatic() {
        return this.type === LanguageMode.Static;
    }

    public isExecutable() {
        return this.type === LanguageMode.Code;
    }
}
