import {Cell} from "./cell.model";
export class Menu {
    public entries: MenuEntry[];
    public inputsEnabled: boolean;
    public codeEnabled: boolean;
    public outputEnabled: boolean;
    public optionsEnabled: boolean;

    constructor() {
        this.inputsEnabled = true;
        this.codeEnabled = true;
        this.outputEnabled = true;
        this.optionsEnabled = true;
        this.entries = [];
    }
}

export class MenuEntry {
    public label: string;
    public eventName: string;
}

export class MenuEvent {
    public name: string;
    public cell: Cell;
}