export class Results {
    public type: string;

    // TODO: We should see if we can standardize this to a common interface
    public content: any;

    constructor(obj?: Object) {
        if (obj) {
            this.unmarshal(obj as Results);
        }
    }

    public getType(): string {
        return this.type;
    }

    public getContent(): any {
        return this.content;
    }

    private unmarshal(obj: Results) {
        this.type = obj.type;
        this.content = obj.content;
    }
}