export class VisualizationSettings {
    //TODO: Can we move these settings to the Options object?
    public parameters: any;

    constructor(obj?: Object) {
        if (obj) {
            this.unmarshal(obj as VisualizationSettings);
        }
    }

    private unmarshal(obj: VisualizationSettings) {
        this.parameters = obj.parameters;
    }
}
